package com.ivanova.librarian.Models;

import com.ivanova.librarian.R;

import java.util.ArrayList;

public class BooksInfo {

    public static ArrayList<BookModel> getRecommendedBooks() {
        ArrayList<BookModel> books = new ArrayList<>();
        books.add(new BookModel("Джоан Роулинг", "Гарри Поттер и Философский камень", "1997", "фэнтези", "Одиннадцатилетний мальчик-сирота Гарри Поттер живет в семье своей тетки и даже не подозревает, что он - настоящий волшебник. Но однажды прилетает сова с письмом для него, и жизнь Гарри Поттера изменяется навсегда. Он узнает, что зачислен в Школу Чародейства и Волшебства, выясняет правду о загадочной смерти своих родителей, а в результате ему удается раскрыть секрет философского камня.", R.drawable.book1, 4.3));
        books.add(new BookModel("Джоан Роулинг", "Гарри Поттер и Тайная комната", "1998", "фэнтези", "На втором году обучения в школе волшебников Гарри Поттер откроет в себе связь с Темным Лордом, проникнет вместе с друзьями в скрытые доселе места Хогвартса, а также узнает, почему читать чужие дневники может быть опасно.", R.drawable.book2, 3.5));
        books.add(new BookModel("Джоан Роулинг", "Гарри Поттер и Узник Азкабана", "1999", "фэнтези", "Двенадцать долгих лет в Азкабане - мрачной тюрьме волшебного мира - содержался всем известный узник по имени Сириус Блэк. Его обвиняли в убийстве тринадцати человек и считали наследником лорда Волан-де-Морта. И вот он бежал, и из оставленных им следов ясно, что на этот раз убийца поставил целью избавиться от Гарри Поттера. Теперь Гарри Поттер в опасности, даже за стенами своей волшебной школы, даже в окружении друзей - потому что среди них есть предатель, готовый открыть убийце путь в Хогвартс.", R.drawable.book3, 3.9));
        books.add(new BookModel("Джоан Роулинг", "Гарри Поттер и Кубок огня", "2000", "фэнтези", "Гарри Поттеру предстоит четвертый год обучения в Школе чародейства и волшебства \"Хогвартс\". Новые заклинания, новые зелья, новые учителя, новые предметы... Все это знакомо, и Гарри с нетерпением ожидает начала учебного года. Но на школу внезапно обрушивается потрясающая новость: в этом году в Хогвартсе будет проходить Турнир Трех Волшебников, и конечно же, каждый хочет принять в нем участие.", R.drawable.book4, 4.8));
        books.add(new BookModel("Джоан Роулинг", "Гарри Поттер и Орден Феникса", "2003", "фэнтези", "Гарри Поттер с нетерпением ждет окончания каникул и начала пятого учебного года в Хогвартсе. Юный волшебник как никогда страдает от одиночества, а насмешки и придирки Дурсли стали совсем невыносимыми. К тому же он уверен, Что Волан-де-Морт восстановил силы и скоро начнет действовать. Вас снова ждут опасные и захватывающие приключения, жестокая борьба, верные друзья и волшебный мир Гарри Поттера.", R.drawable.book5, 5.0));
        return books;
    }

    public static ArrayList<BookModel> getBestBooks() {
        ArrayList<BookModel> books = new ArrayList<>();
        books.add(new BookModel("Анджей Сапковский", "Ведьмак. Меч Предназначения", "1992", "фэнтези", "Ведьмак - это мастер меча и мэтр волшебства, ведущий непрерывную войну с кровожадными монстрами, которые угрожают покою сказочной страны. \"Ведьмак\" - это мир на острие меча, ошеломляющее действие, незабываемые ситуации, великолепные боевые сцены.", R.drawable.book6, 4.8));
        books.add(new BookModel("Анджей Сапковский", "Ведьмак. Кровь эльфов", "1994", "фэнтези", "Таинственные силы Предназначения связали ведьмака Геральта с девочкой, принцессой по имени Цирилла. Возможно ли сбежать от своего Предназначения, ставшего судьбой – и нужно ли это Геральту?", R.drawable.book7, 4.2));
        books.add(new BookModel("Клайв С. Льюис", "Лев, колдунья и платяной шкаф", "1950", "фэнтези", "Никто не знал, какую тайну хранит старый платяной шкаф, пока одной маленькой девочке не пришло в голову спрятаться среди шуб и зимних пальто. И тогда произошли удивительные события, открывшие миру чудесную страну - Нарнию.", R.drawable.book8, 4.6));
        books.add(new BookModel("Клайв С. Льюис", "Принц Каспиан", "1951", "фэнтези", "В Нарнии всегда побеждает добро – даже тогда, когда кажется, что чудесный мир может погибнуть навсегда. Спустя год после первого посещения Нарнии четверо детей возвращаются в волшебную страну и не могут её узнать. В чудесный мир проникло зло. Нарния захвачена чужеземцами, и только бывшим королям под силу победить узурпатора Мираза и вернуть трон законному наследнику – принцу Каспиану.", R.drawable.book9, 4.1));
        books.add(new BookModel("Артур Конан Дойл", "Знак четырех", "1890", "детектив", "Действие разворачивается в 1888 году. Лондон. Молодая, напуганная гувернантка Мэри Морстен обращается за помощью к Шерлоку Холмсу с двумя загадками. Первая касалась исчезновения ее отца 10 лет назад, через несколько лет после которого она стала получать по почте редкие жемчужины от неизвестного покровителя. Наконец, таинственный благодетель просит девушку о встрече. Однако встреча Мэри с незнакомцем поднимает лишь новые вопросы у Холмса, а у Мэри вызывает еще больший страх и подозрения. Но непредвиденные трудности не только не смущают гениального сыщика, но еще больше разжигают его спортивный азарт. Необходимо распутать запутанный клубок преступления, где сплелись воедино спрятанное сокровище, жадность, месть и внезапная смерть.", R.drawable.book20, 4.9));
        return books;
    }

    public static ArrayList<BookModel> getPopularBooks() {
        ArrayList<BookModel> books = new ArrayList<>();
        books.add(new BookModel("Джордж Мартин", "Игра престолов", "1996", "фэнтези", "«Игра престолов» – это суровые земли вечного холода и радостные земли вечного лета. Это легенда о лордах и героях, воинах и магах, убийцах и чернокнижниках, которых свело вместе исполнение древнего пророчества. Это история о таинственных воителях, что сражаются мечами из неведомого металла, и о племени странных созданий, что повергают своих врагов в безумие. Это сказание о жестоком принце драконов, готовом на все, дабы вернуть утраченный трон, и ребенке, что блуждает по сумеречному миру меж жизнью и смертью. Это трагедии и предательства, битвы и интриги, поражения и победы. Это игра, в которой ставка высока... При странных обстоятельствах умирает десница владыки Вестероса. Король Роберт со свитой отправляется на север к давнему другу Эддарду Старку, чтобы предложить тому занять место погибшего... Скрывающиеся в вольных городах потомки свергнутой Робертом...", R.drawable.book11, 3.8));
        books.add(new BookModel("Джордж Мартин", "Пир стервятников", "2005", "фэнтези", "В Вестеросе продолжается война и связанная с этим смута. Потеряв отца, который по сути и был правителем, Серсея в роли регентши при своем малолетнем сыне пытается сохранить в своих руках единоличную власть над страной. Она ссорится со всеми, кто пробует ей возражать, и добивается некоторых успехов, но как оказывается — временных. Леди-рыцарь Бриенна ищет в разоренной стране сбежавшую Сансу Старк. Люди Железных островов выбирают себе нового короля и с ним совершают набеги на материковые земли. Арья Старк становится в Браавосе воспитанницей храма Многоликого бога. Страна разорена, но короли и правители продолжают делить власть и земли...", R.drawable.book12, 4.0));
        books.add(new BookModel("Джордж Мартин", "Танец с драконами", "2011", "фэнтези", "\"Танцем драконов\" издавна звали в Семи Королевствах войну. Но теперь война охватывает все новые и новые земли. Война катится с Севера - из-за Стены. Война идет с Запада - с Островов. Войну замышляет Юг, мечтающий посадить на Железный Трон свою ставленницу. И совсем уже неожиданную угрозу несет с Востока вошедшая в силу \"мать драконов\" Дейенерис... Что будет? Кровь и ненависть. Любовь и политика. И прежде всего - судьба, которой угодно было свести в смертоносном танце великие силы.", R.drawable.book13, 3.4));
        books.add(new BookModel("Брэндон Сандерсон", "Путь королей", "2010", "фэнтези", "Рошар – мир во власти великих бурь, сметающих все живое на своем пути. Но есть и то, что страшнее любой великой бури, – это истинное опустошение. Одно лишь его ожидание меняет судьбы целых народов. Сумеют ли люди сплотиться перед лицом страшной угрозы? Найдется ли тот, для кого древняя клятва – жизнь прежде смерти, сила прежде слабости, путь прежде цели – станет чем-то большим, нежели просто слова?", R.drawable.book14, 4.0));
        books.add(new BookModel("Брэндон Сандерсон", "Слова сияния", "2014", "фэнтези", "Вопреки ожиданиям врагов, Каладин Благословенный Бурей не умер рабом, но возглавил личную гвардию великого князя, и теперь на его плечах лежит обязанность защищать самого короля. Убийца в Белом вновь сеет смерть по всему Рошару, шаг за шагом приближаясь к главной цели – Далинару Холину. Шаллан Давар ставит перед собой самую амбициозную цель из всех возможных: предотвратить возвращение Приносящих пустоту, за которым должна последовать гибель всего мира. А в это время в сердце Расколотых равнин паршенди, уставшие от многолетней войны, делают роковой выбор и обращаются за помощью к той самой силе, от которой некогда бежали их предки. Выдержат ли герои новые испытания? Станут ли новыми Сияющими рыцарями? Над Рошаром сгущаются тени: до конца света осталось всего-навсего шестьдесят два дня.", R.drawable.book15, 4.4));
        return books;
    }

    public static ArrayList<BookModel> getNewBooks() {
        ArrayList<BookModel> books = new ArrayList<>();
        books.add(new BookModel("Джейд Дэвлин", "Приятного аппетита, ваше величество!", "2022", "фэнтези", "Если у тебя есть характер и талант, можно добиться многого, даже иметь свой ресторан и звезду Мишлена в неполные сорок. Но это не спасет тебя от попадания в другой мир, где все придется начинать заново. А самый ужас в том, что ты капризная принцесса, у тебя два жениха и никаких приятных перспектив. Ха! Была принцесса, стал поваренок, а дальше разберемся!", R.drawable.book16, 3.2));
        books.add(new BookModel("Джейд Дэвлин", "Не скучайте, ваше величество!", "2022", "фэнтези", "Если у тебя есть характер и талант, можно добиться многого, даже иметь свой ресторан и звезду Мишлена в неполные сорок. Но это не спасет тебя от попадания в другой мир, где все придется начинать заново. А самый ужас в том, что ты капризная принцесса, у тебя два жениха и никаких приятных перспектив. Ха! Была принцесса, стал поваренок, а дальше разберемся!", R.drawable.book17, 3.6));
        books.add(new BookModel("Юлия Эллисон", "Между льдом и пламенем", "2019", "фэнтези", "Мелания всегда мечтала связать свою жизнь с наукой. В три года она уже умела делать простые зелья, а в пять стащила из библиотеки книгу по основным ядам. В десять родные ходили в зеленых пятнах от ее первого неудачного опыта, а в пятнадцать разрешили поступить в Академию Магии. И кто же знал, что после очередного провала, когда она, кажется, уже была на пороге открытия, ее вдруг переведут на домашнее обучение, а отец решит избавиться, выдав замуж? И что значит «жених будет не один»?", R.drawable.book18, 3.1));
        books.add(new BookModel("Юлия Эллисон", "Хранитель", "2018", "фэнтези", "Юная Нови всего лишь человек, девушка, пожелавшая себе лучшей участи, чем просто нищенка. Благодаря своей настойчивости она сумела поступить в Академию драконов. Но драконы не любят людей на своей территории, ей придется стиснуть зубы и сражаться за свое место под солнцем, чтобы не вылететь снова на улицу и доучиться. Молодой принц драконов Рэминиэль красив, богат и знатен. В его постели девушки задерживаются лишь на одну ночь, и его полностью устраивает такая жизнь. Но у короля есть на него свои планы, которым принц не имеет права мешать. Приказ стать Хранителем еще не родившейся золотой драконицы переворачивает весь его мир, грозя порушить жизнь «золотого мальчика». Что же их ждет в этом мире? Горькое разочарование или ослепляющая любовь?", R.drawable.book10, 4.0));
        books.add(new BookModel("Галина Гончарова", "Учиться, влюбиться... убиться?", "2015", "фэнтези", "Случайное совпадение — и Юлька, будущий биолог, становится студенткой лучшего на два мира Магического универа. Казалось бы, все новое, все незнакомое… Или нет? Учеба, дружба, любовь — разве они не одинаковы во всех мирах? Опасности, приключения, заговоры, враги — и это есть везде. Но студенты факультета боевой магии, на который поступила Юлька, трудностей не боятся. На опасность — начхать, приключению — обрадоваться, заговор — раскрыть, врага — убить… все правильно? А если что-то спутаем — друзья помогут. Или любимый человек. Ах, он не человек вовсе? Неважно. Еще раз повторим — студенты факультета боевой магии трудностей не боятся. А значит — вперед!", R.drawable.book19, 3.9));
        return books;
    }
}
